mapper.select:
SELECT dev_source_code as devSourceCode , dev_source_title as devSourceTitle FROM development_source_file_customizing
WHERE root = #{devSourceCode}
WHERE dev_source_code = (select dev.root from development_source_file_customizing dev where dev.dev_source_code = #{devSourceCode})

mapper.insert:
INSERT INTO 
			development_source_file_customizing
			(dev_source_code
			, dev_source_title
			, dev_source_explanation
			, dev_source_type
			, dev_source_enrollment_date
			, dev_source_act_status
			, root
			)
VALUES		(null
			,#{devSourceTitle}
			,#{devSourceExplanation}
			,#{devSourceType}
			,now()
			,'미사용'
			,#{devSourceRoot}
			)
